    key: "docker-webhook",
    eventTypes: [
        "pushed",
        "deleted",
        "promoted",
    ],
    criteria: {
        anyLocal: true,
        anyRemote: false,
        repoKeys: [my_docker_local.key],
        includePatterns: ["foo/**"],
        excludePatterns: ["bar/**"],
