    descriptionAttribute: "description",
    enabledLdap: "ldap_name",
    filter: "(objectClass=groupOfNames)",
    groupBaseDn: "",
    groupMemberAttribute: "uniqueMember",
    groupNameAttribute: "cn",
    strategy: "STATIC",
    subTree: true,
});
