    create_archive=False,
    cron_exp="0 0 12 * * ? *",
    enabled=True,
    exclude_new_repositories=True,
    excluded_repositories=[],
    export_mission_control=True,
    key="backup_config_name",
    retention_period_hours=1000,
    send_mail_on_error=True,
    verify_disk_space=True)
