        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var some_keypair_gpg_1 = new Keypair("some-keypair-gpg-1", KeypairArgs.builder()        
            .pairName(String.format("some-keypair%s", random_id.randid().id()))
            .pairType("GPG")
            .alias("foo-alias1")
            .privateKey(Files.readString(Paths.get("samples/gpg.priv")))
            .publicKey(Files.readString(Paths.get("samples/gpg.pub")))
            .build());

        var some_keypair_gpg_2 = new Keypair("some-keypair-gpg-2", KeypairArgs.builder()        
            .pairName(String.format("some-keypair%s", random_id.randid().id()))
            .pairType("GPG")
            .alias("foo-alias2")
            .privateKey(Files.readString(Paths.get("samples/gpg.priv")))
            .publicKey(Files.readString(Paths.get("samples/gpg.pub")))
            .build());

        var terraform_local_test_rpm_repo_basic = new LocalRpmRepository("terraform-local-test-rpm-repo-basic", LocalRpmRepositoryArgs.builder()        
            .key("terraform-local-test-rpm-repo-basic")
            .yumRootDepth(5)
            .calculateYumMetadata(true)
            .enableFileListsIndexing(true)
            .yumGroupFileNames("file-1.xml,file-2.xml")
            .primaryKeypairRef(artifactory_keypair.some-keypairGPG1().pair_name())
            .secondaryKeypairRef(artifactory_keypair.some-keypairGPG2().pair_name())
            .build(), CustomResourceOptions.builder()
                .dependsOn(                
                    some_keypair_gpg_1,
                    some_keypair_gpg_2)
                .build());

    }
}
