import * as artifactory from "@pulumi/artifactory";

const release_bundle_webhook = new artifactory.ReleaseBundleWebhook("release-bundle-webhook", {
    criteria: {
        anyReleaseBundle: false,
        excludePatterns: ["bar/**"],
        includePatterns: ["foo/**"],
        registeredReleaseBundleNames: ["bundle-name"],
    },
    eventTypes: [
        "created",
        "signed",
        "deleted",
    ],
    handlers: [{
        customHttpHeaders: {
            "header-1": "value-1",
            "header-2": "value-2",
        },
