    criteria: {
        anyReleaseBundle: false,
        excludePatterns: ["bar/**"],
        includePatterns: ["foo/**"],
        registeredReleaseBundleNames: ["bundle-name"],
    },
    eventTypes: [
        "created",
        "signed",
        "deleted",
    ],
    handlers: [{
        customHttpHeaders: {
            "header-1": "value-1",
            "header-2": "value-2",
        },
        proxy: "proxy-key",
        secret: "some-secret",
        url: "http://tempurl.org/webhook",
    }],
