		})
		if err != nil {
			return err
		}
		_, err = artifactory.NewLocalRpmRepository(ctx, "terraform-local-test-rpm-repo-basic", &artifactory.LocalRpmRepositoryArgs{
			Key:                     pulumi.String("terraform-local-test-rpm-repo-basic"),
