			SecondaryKeypairRef:     pulumi.Any(artifactory_keypair.Some - keypairGPG2.Pair_name),
		}, pulumi.DependsOn([]pulumi.Resource{
			some_keypair_gpg_1,
			some_keypair_gpg_2,
		}))
		if err != nil {
			return err
		}
		return nil
	})
}
