    build: {
        actions: {
            users: [{
                name: "anonymous",
                permissions: [
                    "read",
                    "write",
                ],
            }],
        },
        includesPatterns: ["**"],
        repositories: ["artifactory-build-info"],
    },
    repo: {
        actions: {
            groups: [{
                name: "readers",
                permissions: ["read"],
            }],
            users: [{
                name: "anonymous",
                permissions: [
                    "read",
                    "write",
                ],
            }],
        },
        excludesPatterns: ["bar/**"],
        includesPatterns: ["foo/**"],
        repositories: ["example-repo-local"],
    },
});
