        var my_file = Output.Create(Artifactory.GetFile.InvokeAsync(new Artifactory.GetFileArgs
        {
            OutputPath = "tmp/artifact.zip",
            Path = "/path/to/the/artifact.zip",
            Repository = "repo-key",
        }));
    }

}
